application/json.schema:
{
	"$schema": "http://json-schema.org/schema",
	"type": "object",
	"description": "CompoSR client login response",
	"properties": {
		"accessToken": {
			"type": "string",
			"descrption": "accessToken to send in every authorized request"
		},
		"expiresAt": {
			"type": "number",
			"descrption": "epoch time until token expiration"
		}
	},
	"required": ["accessToken", "expiresAt"]
}

{
	"$schema": "http://json-schema.org/schema",
	"type": "object",
	"description": "CompoSR error response",
	"properties": {
		"httpStatus": {
			"type": "number",
			"descrption": "HTTP status code"
		},
		"error": {
			"type": "string",
			"descrption": "error code"
		},
		"errorDescription": {
			"type": "string",
			"descrption": "Public description"
		},
		"trace": {
			"type": "string",
			"descrption": "Development info description"
		}
	},
	"required": ["httpStatus", "error", "errorDescription"]
}
