application/json.schema:
{
	"$schema": "http://json-schema.org/schema",
	"type": "object",
	"description": "CompoSR login response",
	"properties": {
		"token": {
			"type": "object",
			"description": "CompoSR login response",
			"properties": {
				"accessToken": {
					"type": "string",
					"descrption": "accessToken to send in every authorized request"
				},
				"expiresAt": {
					"type": "number",
					"descrption": "epoch time until token expiration"
				},
				"refreshToken": {
					"type": "string",
					"descrption": "refreshToken to send to renew an accessToken"
				}
			}
		},
		"user": {
			"type": "object",
			"description": "User object",
			"properties": {
				"firstName": {
					"type": "string",
					"description": "User firstName"
				},
				"lastName": {
					"type": "string",
					"description": "User lastName"
				},
				"email": {
					"type": "string",
					"description": "User email"
				},
				"properties": {
					"type": "object",
					"description": "Any custom propierties attached to user"
				}
			}
		}
	},
	"required": ["token", "user"]
}
